mapper.update:
update class
   set CL_STATUS = 'Y'
 where clno = #{clNo}

update class
   set CL_STATUS = 'R',
   	   REASON = #{reason}
 where clno = #{clNo}

mapper.select:
select count(*) from class_review join member using (mno) where clno = #{clNo}
and C_RNO = #{keyword}
and nickname like '%' || #{keyword} || '%'
and CONTENT like '%' || #{keyword} || '%'

select c_rno , clno , nickname , content , FILEPATH , to_char(create_date, 'YYYY-MM-DD') as create_date , status from class_review join member using (mno) where clno = #{clNo}
and C_RNO = #{keyword}
and nickname like '%' || #{keyword} || '%'
and CONTENT like '%' || #{keyword} || '%'
order by CREATE_DATE desc
order by CREATE_DATE

mapper.update:
update class
   set CL_STATUS = 'Y'
 where clno = #{clNo}

update class
   set CL_STATUS = 'D'
 where clno = #{clNo}

update class_review
   set STATUS = 'Y'
 where C_RNO = #{crNo}

update class_review
   set STATUS = 'N'
 where C_RNO = #{crNo}

mapper.select:
select count(*) from cs_qna JOIN member USING (MNO) where GB_NO = 1 and REFER_NO = #{clNo}
and CS_QNO = #{keyword}
and nickname like '%' || #{keyword} || '%'
and TITLE like '%' || #{keyword} || '%'
and CONTENT like '%' || #{keyword} || '%'
