mapper.insert:
insert
  into notice
  (
    NNO
  , MNO
  , CATEGORY
  , N_TITLE
  , N_CONTENT
  , IMG
  )
  values
  (
    seq_nno.nextval
  , #{memNo}
  , #{category}
  , #{noTitle}
  , #{noContent}
  , #{img}
  )

mapper.update:
		update
        	   notice
		   set category = #{category}
		   	 , n_title = #{noTitle}
		     , n_content = #{noContent}
		     , img = #{img} 
		 where nno = #{noNo}

mapper.insert:
insert
  into FAQ
  (
    FNO
  , MNO
  , CATEGORY
  , F_TITLE
  , F_CONTENT
  )
  values
  (
    seq_fno.nextval
  , #{memNo}
  , #{category}
  , #{faqTitle}
  , #{faqContent}
  )

mapper.update:
		update
        	   faq
		   set category = #{category}
		   	 , f_title = #{faqTitle}
		     , f_content = #{faqContent}
		 where fno = #{faqNo}
