mapper.insert:
insert 
  into COUPONLIST 
     (
       CNO
     , CNAME
     , DISCOUNT
     , DEDATE
     , maxcount
     )
values
     (
       SEQ_CNO.nextval
     , #{couponName}
     , #{discount}
     , #{dedate}
     , #{maxCount}
     )

insert  
  into COUPON
     (
       pcno
     , mno
     , cno
     , dedate
     )
values
     (
       SEQ_CPNO.nextval
     , #{memNo}
     , #{couponNo}
     , #{deDate}
     )

mapper.update:
update couponlist
   set (
          cname = #{couponName}
        , discount = #{discount}
        , maxcount = #{maxCount}
        , dedate = #{dedate}
        , endate = sysdate
        )
where cno = #{couponNo}

update pay
   set order_status = 6
 where order_no = #{orderNo}

update pay
   set order_status = #{orderStatus}
 where order_no = #{orderNo}
