mapper.select:
select count(*)
  from like_item
 where mno = #{memNo}
   and refer_no = #{referNo}

mapper.delete:
delete 
  from like_item
 where mno = #{memNo}
   and refer_no = #{referNo}

mapper.insert:
insert 
  into reply
values
	 ( 
	   SEQ_CMNO.NEXTVAL
	 , #{memNo}
	 , 2
	 , #{contentNo}
	 , #{replyContent}
	 , sysdate
	 , 'Y'
	 )

mapper.select:
select count(*)
  from recommend
 where gb_no = 1
   and refer_no = #{crNo}
   and mno = #{memNo}

mapper.delete:
delete 
  from recommend
 where mno = #{memNo}
   and refer_no = #{crNo}

mapper.update:
update reply
   set REPLY_STATUS = 'N'
 where REPLY_NO = #{replyNo}

update reply
   set reply_content = #{replyContent}
 where reply_status = 'Y'
   and reply_no = #{replyNo}

mapper.select:
select count(*)
  from class_order
 where reading_status = 'Y'
   and clNo = #{clNo}
   and mno = #{memNo}

mapper.insert:
insert
  into class_review
values
	 (
	   SEQ_CRNO.NEXTVAL
	 , #{clNo}
	 , #{memNo}
	 , #{content}
	 , #{star}


	 		, #{filePath}


	 		, null


	 , sysdate
	 , 'Y'
	 )

mapper.update:
update class_review
   set status = 'N'
 where c_rno = #{crNo}

update class_review
   set content = #{content},
   	   star = #{star}

   	   		, filePath = #{filePath}


   	   		, filePath = null

 where c_rno = #{crNo}
   and status = 'Y'

mapper.insert:
insert
  into cs_qna
values
	 (
	   SEQ_CSQNO.NEXTVAL
	 , #{memNo}
	 , 1
	 , #{referNo}
	 , #{title}
	 , #{content}
	 , sysdate


	 		, #{pwd}


	 		, null




	 		, #{filePath}


	 		, null


	 , 'Y'
	 , null
	 , null
	 )

mapper.update:
update cs_qna
   set title = #{title}
     , content = #{content}


		   , pwd = #{pwd}


		   , pwd = null



   	   	   , filePath = #{filePath}


   	   	   , filePath = null

 where status = 'Y'
   and cs_qno = #{csQnaNo}

mapper.select:
select count(*)
  from cs_qna
 where STATUS = 'Y'
   and PWD = #{pwd}
   and cs_qno = #{csQnaNo}

select count(*)
  from class_order
 where READING_STATUS != 'E'
   and clNo = #{clNo}
   and mno = #{memNo}

mapper.insert:
insert 
  into class_order
values
	 (
	   'C' || to_char(sysdate, 'YYYYMMDD') || SEQ_CONO.NEXTVAL
	 , #{clNo}
	 , #{memNo}
	 , #{price}


			, null 		 	


			, #{pointUse}


	 , #{pcNo}
	 , #{amount}
	 , sysdate
	 , #{paymentOption}


	 		, 'N'


	 		, null




	 		, #{amount} * 0.01


	 		, null


	 , 'N'
	 )

mapper.update:
update coupon
   set use = 'Y'
 where pcno = #{pcNo}

update member
   set point = point - #{pointUse}
 where mno = #{memNo}

update member
   set point = point + #{pointSave}
 where mno = #{memNo}
