mapper.select:
select count(*)
  from like_item
 where mno = #{memNo}
   and refer_no = #{referNo}

mapper.delete:
delete 
  from like_item
 where mno = #{memNo}
   and refer_no = #{referNo}

mapper.insert:
insert 
  into reply
values
	 ( 
	   SEQ_CMNO.NEXTVAL
	 , #{memNo}
	 , 2
	 , #{contentNo}
	 , #{replyContent}
	 , sysdate
	 , 'Y'
	 )

mapper.select:
select count(*)
  from recommend
 where gb_no = 1
   and refer_no = #{crNo}
   and mno = #{memNo}

mapper.delete:
delete 
  from recommend
 where mno = #{memNo}
   and refer_no = #{crNo}

mapper.update:
update reply
   set REPLY_STATUS = 'N'
 where REPLY_NO = #{replyNo}

update reply
   set reply_content = #{replyContent}
 where reply_status = 'Y'
   and reply_no = #{replyNo}

mapper.select:
select count(*)
  from class_order
 where reading_check = 'Y'
   and clNo = #{clNo}
   and mno = #{memNo}

mapper.insert:
insert
  into class_review
values
	 (
	   SEQ_CRNO.NEXTVAL
	 , #{clNo}
	 , #{memNo}
	 , #{content}
	 , #{star}


	 		, #{filePath}


	 		, null


	 , sysdate
	 , 'Y'
	 )

mapper.update:
update class_review
   set status = 'N'
 where c_rno = #{crNo}

update class_review
   set content = #{content},
   	   star = #{star}

   	   		, filePath = #{filePath}


   	   		, filePath = null

 where c_rno = #{crNo}
   and status = 'Y'
