mapper.insert:
insert 
  into qna
     (
       qno
     , mno
     , category
     , q_title
     , q_content
     )
values
     (
       seq_qno.nextval
     , #{memNo}
     , #{category}
     , #{qnaTitle}
     , #{qnaContent}
     )

