mapper.insert:
  		insert 
		  into STORE
		     (
		       pno
		     , mno
		     , category
		     , title
		     , price
		     , stock
		     , endate
		     , courier
		     , soldout_yn
		     , status
		     , img1
		     , img2
		     , img3
		     , img4
		     )
		 VALUES
		    (
		      SEQ_SNO.NEXTVAL
		    , #{memberNo}
		    , #{category}
		    , #{title}
		    , #{price}
		    , #{stock}
		    , sysdate
		    , #{courier}
		    , 'N'
		    , 'N'
		    , #{productImg0}
		    , #{productImg1}
		    , #{productImg2}
		    , #{productImg3}
		    )

mapper.update:
  		update store
		   set  category = #{category}
		      , title = #{title}
		      , price = #{price}
		      , courier = #{courier}
		      , img1 = #{productImg0}
		      , img2 = #{productImg1}
		      , img3 = #{productImg2}
		      , img4 = #{productImg3}
		 where pno = #{productNo}

  		update soption
		   set  opname = #{optionName}
		      , price = #{price}
		 where opno = #{optionNo}

mapper.insert:
  		insert 
		  into SOPTION
		     (
		       opno
		     , pno
		     , opname
		     , price
		     )
		 VALUES
		    (
		      SEQ_OPNO.NEXTVAL
		    , #{productNo}
		    , #{optionName}
		    , #{price}
		    )

mapper.update:
  		update store
		   set status = 'D'
		 where pno = #{pno}

  		update store
		   set stock = stock+#{inValue} 
		     , soldout_yn = #{soldout}
		 where pno = #{productNo}

mapper.insert:
  		insert 
