		      )
		 values
		      (
		        SEQ_SIONO.nextval
		      , #{productNo}
		      , #{title}
		      , #{stock}
		      , #{quantity}
		      , #{unitPrice}
		      , #{total}
		      , #{orderSite}
		      )

mapper.update:
update pay
   set order_status = #{orderStatus}
 where order_no = #{orderNo}

