mapper.update:
update
	   member
   set nickname = #{nickName}
   	 , phone = #{phone}
   	 , profile = #{profileImg}
 where mno = #{memNo}

update
	   member
   set user_pwd = #{userPwd}
 where mno = #{memNo}

mapper.select:
select count(*)
  from like_item
 where mno = #{memNo}

select count(*)
  from class_review
 where mno = #{mno}
