mapper.select:
select
	   count(*)
  from like_item
 where mno = #{memNo}
   and refer_no = #{referNo}

mapper.delete:
delete 
  from like_item
 where mno = #{memNo}
   and refer_no = #{referNo}

mapper.select:
select
	   count(*)
  from cart
 where mno = #{memberNo}
   and pno = #{productNo}

mapper.delete:
delete 
  from cart
 where mno = #{memberNo}
   and pno = #{productNo}

mapper.update:
		update
			   cart
		   set quantity = (
		   				  select
                                 quantity
                            from cart
                           where mno = #{memberNo}
                             and pno = #{productNo}
                           ) + 1
		 where mno = #{memberNo}
		   and pno = #{productNo}

mapper.insert:
insert
  into s_review
values
	 (
	   SEQ_SRNO.NEXTVAL
	 , #{memberNo}
	 , #{productNo}
	 , #{content}
	 , #{rating}
	 , SYSDATE
	 , 'Y'


	 		, #{img}


	 		, null


	 )

mapper.select:
select count(*)
  from recommend
 where gb_no = 2
   and refer_no = #{reviewNo}
   and mno = #{memberNo}

mapper.delete:
delete 
  from recommend
 where mno = #{memberNo}
   and refer_no = #{reviewNo}

mapper.insert:
insert
  into report
values
	 (
	   seq_rpno.nextval
	 , #{reportMemNo}
     , 'SR'
	 , #{refBNo}
	 , #{content}
	 , 'y'
	 , SYSDATE
	 )

insert
  into cs_qna
values
	 (
	   SEQ_CSQNO.NEXTVAL
	 , #{memNo}
	 , 2
	 , #{referNo}
	 , '(' || #{category} || ')' || #{title}
	 , #{content}
	 , sysdate


	 		, #{pwd}


	 		, null


	 , null
	 , 'Y'
	 , null
	 , null
	 )

mapper.select:
select count(*)
  from cs_qna
 where STATUS = 'Y'
   and PWD = #{pwd}
   and cs_qno = #{csQno}

mapper.update:
update s_review set content=#{content}, star_rating=#{rating},
filepath=#{img}
filepath=null
where rno = #{reviewNo}
